%flink

val t = stenv.from("cdn_access_log")
   .select("uuid, ip_to_country(client_ip) as country, response_size, request_time")
   .groupBy("country")
   .select( "country, count(uuid) as access_count, sum(response_size) as total_download,  sum(response_size) * 1.0 / sum(request_time) as download_speed")
   //.insertInto("cdn_access_statistic")

// z.show will display the result in zeppelin front end in table format, you can uncomment the above insert statement and the below st_env.execute in order to write the data to mysql table.
z.show(t, streamType="update")
